0d1271c7-6d97-4500-b709-d8847086d848.template:
{{#if (eval type "==" "STORY")}}
{{#if (hasInlinks "satisfaction")}}
{{#eachInlinkWith type="satisfaction"}}
  <p>&#x25C2; {{#hyperlink}}{{docObjId}}{{/hyperlink}}: {{shortDescription  maxLength=80}}
{{#if (hasInlinks "verification")}}
{{#eachInlinkWith type="verification"}}
    <br>&emsp;&#x25C2; {{#hyperlink}}{{docObjId}}{{/hyperlink}}: {{shortDescription  maxLength=80}}
{{/eachInlinkWith}}
{{else}}
    <br>&emsp;&#x25C2; <em>Missing verification links!</em>
{{/if}}
  </p>
{{/eachInlinkWith}}
{{else}}
  <p>&#x25C2; <em>Missing satisfaction links!</em></p>
{{/if}}
{{/if}}

