283578a7-4cef-47a9-b7ef-68d9f81038a7.template:
{{#if (eval type "==" "ACT")}}
{{#if (hasInlinks "mitigation")}}
{{#eachInlinkWith type="mitigation"}}
 <p>&#x25C2; {{#hyperlink}}{{docObjId}}{{/hyperlink}}: {{shortDescription  maxLength=80}}</p>
{{#if (hasInlinks "verification")}}
{{#eachInlinkWith type="verification"}}
  <p style="margin-left:15px">&#x25C2; {{#hyperlink}}{{docObjId}}{{/hyperlink}}: {{shortDescription  maxLength=80}}</p>
{{/eachInlinkWith}}
{{else}}
  <p style="margin-left:15px"><em>Missing verification links!</em></div>
{{/if}}
{{/eachInlinkWith}}
{{else}}
  <p><em>Missing mitigation links!</em></div>
{{/if}}
{{/if}}

74b9b882-3a3e-4f19-891e-c993faa3a574.template:
{{#if (eval (eval type "==" "CAUSE") "||" (eval type "==" "ACT"))}}
{{var "rpn" (eval (eval detectability "*" probability) "*" severity)}}
<div style="margin-top:7px; font-weight:bold; text-align:center; {{#if (eval (get "rpn") ">" 100)}}background-color:lightsalmon;{{/if}}">
   {{get "rpn"}}
</div>
{{/if}}
